/**
 * Adminhtml transaction details grid
 *
 * @category   Mage
 * @package    Mage_Adminhtml
 * @author     Magento Core Team <core@magentocommerce.com>
 */
class Phoenix_BankPayment_Block_Adminhtml_Sales_Transactions_Detail_Grid extends Mage_Adminhtml_Block_Sales_Transactions_Detail_Grid
{
    /**
     * Retrieve Transaction addtitional info
	 * Overwritten to fix a weird magento "bug". The default method is hardcoded
	 * to always retrieve the same key
     *
     * @return array
     */
    public function getTransactionAdditionalInfo()
    {
        $info = Mage::registry('current_transaction')->getAdditionalInformation();
        return (is_array($info)) ? $info : array();
    }
}
